import React, { useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import Home from "./Home";
import Friends from "./Friends";
import YourPosts from "./YourPosts";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Mintr</h1>
        <input
          type="text"
          placeholder="Search users..."
          value={searchQuery}
          onChange={handleSearchChange}
          className="search-bar"
        />
      </header>

      <nav className="dashboard-nav">
        <Link to="/dashboard" className="nav-link">
          Home
        </Link>
        <Link to="/dashboard/friends" className="nav-link">
          Friends
        </Link>
        <Link to="/dashboard/your-posts" className="nav-link">
          Your Posts
        </Link>
      </nav>

      <main className="dashboard-main">
        <Routes>
          <Route path="/" element={<Home searchQuery={searchQuery} />} />
          <Route path="/friends" element={<Friends searchQuery={searchQuery} />} />
          <Route path="/your-posts" element={<YourPosts searchQuery={searchQuery} />} />
        </Routes>
      </main>
    </div>
  );
};

export default Dashboard;
