import React, { useState } from "react";

// The main App component that contains all the logic and sub-components.
const App = () => {
  // State for the current page, used for simple, in-file navigation.
  const [currentPage, setCurrentPage] = useState("Home");
  // State for the search query, passed down to child components.
  const [searchQuery, setSearchQuery] = useState("");

  // Function to handle changes in the search input field.
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // The Dashboard component, which is the main layout.
  const Dashboard = () => {
    return (
      <div>
        {/* Sidebar */}
        <aside>
          <div>
            {/* Search Input */}
            <input
              type="text"
              placeholder="Search users or posts..."
              value={searchQuery}
              onChange={handleSearchChange}
            />
            {/* Navigation Links */}
            <nav>
              <button
                onClick={() => setCurrentPage("Home")}
              >
                Home
              </button>
              <button
                onClick={() => setCurrentPage("Friends")}
              >
                Friends
              </button>
              <button
                onClick={() => setCurrentPage("YourPosts")}
              >
                Your Posts
              </button>
            </nav>
          </div>
        </aside>

        {/* Main Content Area */}
        <main>
          {/* Conditional rendering based on the currentPage state */}
          {(() => {
            switch (currentPage) {
              case "Home":
                return <Home searchQuery={searchQuery} />;
              case "Friends":
                return <Friends searchQuery={searchQuery} />;
              case "YourPosts":
                return <YourPosts searchQuery={searchQuery} />;
              default:
                return <Home searchQuery={searchQuery} />;
            }
          })()}
        </main>
      </div>
    );
  };

  // Home Component Placeholder
  const Home = ({ searchQuery }) => (
    <div>
      <h2>Home</h2>
      <p>
        Welcome to your dashboard! Search query: "{searchQuery}".
      </p>
    </div>
  );

  // Friends Component Placeholder
  const Friends = ({ searchQuery }) => (
    <div>
      <h2>Friends</h2>
      <p>
        Here are your friends. Search query: "{searchQuery}".
      </p>
    </div>
  );

  // YourPosts Component with functional search
  const YourPosts = ({ searchQuery }) => {
    // Hardcoded sample data for a user's posts
    const posts = [
      {
        id: 1,
        title: "My First Post on This Platform",
        content: "I'm excited to share my thoughts and connect with others here!",
      },
      {
        id: 2,
        title: "A Day in the Life of a Coder",
        content: "Today I wrote a lot of code, fixed a few bugs, and drank too much coffee. A productive day!",
      },
      {
        id: 3,
        title: "Hiking Adventure",
        content: "Just got back from a beautiful hike. The mountain views were incredible. So glad I went!",
      },
      {
        id: 4,
        title: "React is so much fun!",
        content: "Loving the new hooks and state management patterns. It makes building UIs a breeze.",
      },
      {
      id: 5,
        title: "Weekend Plans",
        content: "Looking forward to a relaxing weekend. Maybe I'll finally finish that book I started.",
      },
    ];

    // Filter posts based on the searchQuery prop
    const filteredPosts = posts.filter(
      (post) =>
        post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        post.content.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
      <div>
        <h2>Your Posts</h2>
        {filteredPosts.length > 0 ? (
          <div>
            {filteredPosts.map((post) => (
              <div
                key={post.id}
              >
                <h3>
                  {post.title}
                </h3>
                <p>{post.content}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>
            No posts match your search query: "{searchQuery}".
          </p>
        )}
      </div>
    );
  };

  return <Dashboard />;
};

export default App;
