import React, { useState } from "react";
import { FiSettings } from "react-icons/fi"; // Settings icon
import '../styles/Dashboard.css'; // Import the CSS file

const Dashboard = () => {
  const [currentPage, setCurrentPage] = useState("Home");
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Sidebar navigation component
  const Sidebar = () => (
    <aside>
      <input
        type="text"
        placeholder="Search users..."
        value={searchQuery}
        onChange={handleSearchChange}
      />
      <nav>
        <button onClick={() => setCurrentPage("Home")}>Home</button>
        <button onClick={() => setCurrentPage("Friends")}>Friends</button>
      </nav>
    </aside>
  );

  // Settings icon component
  const Settings = () => (
    <div className="settings-icon">
      <FiSettings size={24} />
    </div>
  );

  // Home component
  const Home = ({ searchQuery }) => (
    <div>
      <h2>Home</h2>
      <p>Welcome to your dashboard! Search query: "{searchQuery}"</p>
    </div>
  );

  // Friends component
  const Friends = ({ searchQuery }) => (
    <div>
      <h2>Friends</h2>
      <p>Your friends list. Search query: "{searchQuery}"</p>
    </div>
  );

  // Main content rendering
  const MainContent = () => {
    switch (currentPage) {
      case "Home":
        return <Home searchQuery={searchQuery} />;
      case "Friends":
        return <Friends searchQuery={searchQuery} />;
      default:
        return <Home searchQuery={searchQuery} />;
    }
  };

  return (
    <div className="dashboard">
      <Sidebar />
      <main>
        <Settings />
        <MainContent />
      </main>
    </div>
  );
};

export default Dashboard;
